mapper.select:
select a.DATASOURCE_ID, a.DATASOURCE_NAME, a.DATABASE_TYPE, a.CONNPOOL_TYPE, a.DATASOURCE_URL, a.USER_NAME, a.PASSWORD, a.INITIAL_SIZE, a.MAX_ACTIVE, a.MAX_WAIT, a.MAX_IDLE, a.REMOVE_STATUS, a.CREATED_BY, a.CREATED_DT, a.UPDATED_BY, a.UPDATED_DT, a.DATASOURCE_CHINESE_NAME from rpt_datasource a left join rpt_data_Table b on a.DATASOURCE_ID = b.DATASOURCE_ID where 1 = 1
AND table_id in
#{item}

mapper.delete:
UPDATE rpt_datasource
SET REMOVE_STATUS = 1
WHERE DATASOURCE_ID = #{datasourceId}

mapper.select:
select

from `rpt_datasource`


        and DATASOURCE_ID = #{datasourceId,jdbcType=BIGINT}


        and DATASOURCE_NAME like CONCAT('%', #{datasourceName,jdbcType=VARCHAR}, '%')


        and DATABASE_TYPE = #{databaseType,jdbcType=VARCHAR}


        and CONNPOOL_TYPE = #{connpoolType,jdbcType=VARCHAR}


        and REMOVE_STATUS = #{removeStatus,jdbcType=TINYINT}


        and DATASOURCE_CHINESE_NAME = #{datasourceChineseName,jdbcType=VARCHAR}


order by DATASOURCE_ID desc

select

from `rpt_datasource`


        and DATASOURCE_NAME = #{datasourceName,jdbcType=VARCHAR}


        and DATASOURCE_CHINESE_NAME = #{datasourceChineseName,jdbcType=VARCHAR}


order by DATASOURCE_ID desc
